            \edef\tc{\fpeval{\na*\nc}}
            % Calcul de l'exposant commun (minimum de \puisxb et \puisxc)
            \edef\factorexp{\minnum{\puisxb}{\puisxc}}%
            % Calcul du PGCD (en valeur absolue) des coefficients de la parenthèse
            \edef\factornum{\gcdcalc{\absnum{\tb}}{\absnum{\tc}}}%
            % Calcul des coefficients restants une fois le PGCD extrait
            %   
            \providecommand{\checkSigns}{%
                \def\tempSigne{-}%
                \ifx\signe\tempSigne
                    \ifnum\nb<0
                        \edef\factornum{-\factornum}%
                        \def\fsigne{+}%
                    \fi
                    \ifnum\nb>0
                        \ifnum\nc<0
                            \def\fsigne{}
                        \fi
                        \ifnum\nc>0
                            \def\fsigne{}
                        \fi
                    \fi
                \else
                    \def\fsigne{+}%
                \fi
            }%
            \checkSigns
